exploreUrl:
@js:
let url = String(source.getVariable()!=""?source.getVariable():source.getKey()).replace(/\/$/, '') 
let arr = []
let push = (title, url, type) => arr.push({
  title: title,
  url: url,
  style: {
    layout_flexGrow: 1,
    layout_flexBasisPercent: type
  }
})

let res = org.jsoup.Jsoup.parse(java.ajax(url))
let lis = res.select('.nav li');
for (let i = 0; i < lis.length; i++) {
    let text = lis[i].select('a').text();
    let href = url + lis[i].select('a').attr('href');
    if (i === 0) push(text, href, 1);
    else push(text, href, 0.25);
}
JSON.stringify(arr)

