function login () {
  try {
    let device = java.randomUUID()
    let regurl = 'https://look.66shuwu.com/api/v1/account/deviceRegister'

    let body = JSON.stringify({
      device,
      invitationCode: 'https://www.xpshuku.com/'
    })

    let headers = {
      'User-Agent': 'okhttp/4.7.2',
      'Content-Type': 'application/json'
    }

    let response = java.post(regurl, body, headers).body()
    let tokenData = JSON.parse(response)

    if (tokenData && tokenData.data && tokenData.data.token) {
      let token = JSON.stringify({ token: tokenData.data.token })
      source.putLoginHeader(token)
      java.toast('‚úÖÁôªÂΩïÊàêÂäü')
